import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { ReactNode ,useState,useCallback } from "react"
import ReactFlow, { Controls, Background ,MiniMap, applyEdgeChanges, applyNodeChanges} from 'reactflow';
import 'reactflow/dist/style.css';

interface Node {
  id: string;
  type?: string;
  position: { x: number; y: number };
  data: { label: string };  
}

interface State {
  numClicks: number;
  isFocused: boolean;
  nodes: Node[]; // Adding nodes to the state
  lastNumClicks : number;
}


/**
 * This is a React-based component template. The `render()` function is called
 * automatically when your component should be re-rendered.
 */
class MyComponent extends StreamlitComponentBase<State> {

  public state : State = { numClicks: 0, 
                isFocused: false,
                nodes: [
                  // Initialize with a default node or leave empty if not needed
                  {
                    id: '1',
                    position: { x: 250, y: 5 },
                    data: { label: 'Initial Node' },
                  },
                  
                ],
                lastNumClicks: 0,
              }; 
  addNode(label: string) {
                const newNode: Node = {
                  id: `node-${this.state.nodes.length + 1}`,
                  position: { x: Math.random() * 400, y: Math.random() * 400 },
                  data: { label },
                };
              
                this.setState(prevState => ({
                  nodes: [...prevState.nodes, newNode],
                }));
              }

  onNodesChange = (changes:any) => {
                this.setState((state) => ({
                  nodes: applyNodeChanges(changes, state.nodes),
                }));
              }
              
  public render = (): ReactNode => {
    // Arguments that are passed to the plugin in Python are accessible
    // via `this.props.args`. Here, we access the "name" arg.
    
    const name = this.props.args["name"]
    const { numClicks } = this.props.args;
    const { lastNumClicks } = this.state;
    const flowStyles = {"width": '100%', "height": 500}

    // Streamlit sends us a theme object via props that we can use to ensure
    // that our component has visuals that match the active theme in a
    // streamlit app.
    const { theme } = this.props
    const style: React.CSSProperties = {}
    

    // Maintain compatibility with older versions of Streamlit that don't send
    // a theme object.
    if (theme) {
      // Use the theme object to style our button border. Alternatively, the
      // theme style is defined in CSS vars.
      const borderStyling = `1px solid ${
        this.state.isFocused ? theme.primaryColor : "gray"
      }`
      style.border = borderStyling
      style.outline = borderStyling
    }
    if (numClicks !== lastNumClicks) {
      this.setState({ lastNumClicks: numClicks }); // Update lastNumClicks in state
      this.addNode(this.props.args["label"] || `Node ${this.state.nodes.length + 1}`);
    }
    
    // Show a button and some text.
    // When the button is clicked, we'll increment our "numClicks" state
    // variable, and send its new value back to Streamlit, where it'll
    // be available to the Python program.
    return (
      <div style={{ height: 1000 ,width : 1000}}>
        <h3> {name} </h3>
      <ReactFlow nodes={this.state.nodes}
      onNodesChange={this.onNodesChange}>
      <MiniMap nodeStrokeWidth={3} />
        <Background />
        <Controls />
      </ReactFlow>
    </div>

    )
  }

}
// "withStreamlitConnection" is a wrapper function. It bootstraps the
// connection between your component and the Streamlit app, and handles
// passing arguments from Python -> Component.
//
// You don't need to edit withStreamlitConnection (but you're welcome to!).
export default withStreamlitConnection(MyComponent)
